function plugin_autosize(arg) {
    return arg.autosize();
}

function plugin_watermark(el, txt) {
    el.watermark(txt);
}

function plugin_expand(elements, readmoretxt) {
    for (var i = 0; i < elements.size() ; i++) {
        var el = elements.eq(i);
        var lineHeight = parseInt(el.css('line-height'), 10);
        var maxHeight = el.hasClass('comment_text') ?
            3 * lineHeight :
            (el.find('iframe').size() > 0 ?
                (6 * lineHeight) + el.find('iframe').eq(0).height() :
                6 * lineHeight);
        el.readmore({
            speed: 25,
            maxHeight: maxHeight,
            moreLink: '<a href="#">' + readmoretxt + '</a>',
            lessLink: ''
        });
    }
}

function plugin_autocomplete(el, url) {
    el.autocomplete({
        serviceUrl: url,
        noCache: true
    });
}

function plugin_mask(el, msk) {
    el.mask(msk);
}

