function plugin_player(id) {
    flowplayer(id, "/Scripts/flowplayer/flowplayer-3.2.18.swf", {
        canvas: { backgroundColor: "transparent" },
        plugins: {
            controls: {
                timeBgColor: '#555555',
                disabledWidgetColor: '#555555',
                timeColor: '#01DAFF',
                bufferColor: '#445566',
                autoHide: 'never',
                timeBorder: '1px solid rgba(0, 0, 0, 0.3)',
                callType: 'default',
                buttonColor: '#5F747C',
                borderRadius: '0px',
                tooltipColor: '#5F747C',
                buttonOverColor: '#728B94',
                durationColor: '#ffffff',
                backgroundGradient: 'medium',
                bufferGradient: 'none',
                tooltipTextColor: '#ffffff',
                buttonOffColor: '#747474',
                backgroundColor: '#222222',
                sliderColor: '#000000',
                sliderGradient: 'none',
                progressGradient: 'medium',
                progressColor: '#112233',
                volumeSliderColor: '#000000',
                volumeSliderGradient: 'none',
                height: 32,
                opacity: 1.0
            }
        },
        clip: {
            autoPlay: true,
            autoBuffering: true,
            scaling: 'fit'
        }
    });
}