function show_password_hint(s) { var e = $("#acc_set #password-hint"); e.css("top", s.position().top + "px").css("left", s.position().left - e.width() - 20 + "px").fadeIn("fast") } function hide_hint() { $("#acc_set #password-hint").fadeOut("fast") } function SaveBegin() { $("#acc_set #msg_save").hide() } function SaveFailure() { $("#acc_set #msg_save").removeClass("success_message").addClass("error_message").html($("#acc_set #ajax_fail").val()).fadeIn("fast") } function SaveSuccess(s) { if (s.Done) $("#acc_set #msg_save").removeClass("error_message").addClass("success_message").html(s.Message).fadeIn("fast"); else { var e = $("#msg_errors_title").val(); e += "<ul>"; for (var a = 0; a < s.Errors.length; a++) e += "<li>" + s.Errors[a] + "</li>"; e += "</ul>", $("#acc_set #msg_save").removeClass("success_message").addClass("error_message").html(e).fadeIn("fast") } } $(function () { $('#acc_set input[name="NewPassword"]').live("focus", function () { show_password_hint($(this)) }), $('#acc_set input[name="NewPassword"]').live("blur", function () { hide_hint() }) });