function SaveBegin() { $("#album_editor #msg_save").hide(), $('#album_editor input[type="submit"]').attr("disabled", "disabled") } function SaveFailure() { $("#album_editor #msg_save").removeClass("success_message").addClass("error_message").html($("#album_editor #ajax_fail").val()).fadeIn("fast") } function SaveSuccess(e) { if (e.Done) $("#album_editor #msg_save").removeClass("error_message").addClass("success_message").html(e.Message).fadeIn("fast"); else { var s = $("#msg_errors_title").val(); s += "<ul>"; for (var a = 0; a < e.Errors.length; a++) s += "<li>" + e.Errors[a] + "</li>"; s += "</ul>", $("#album_editor #msg_save").removeClass("success_message").addClass("error_message").html(s).fadeIn("fast") } } function SaveComplete() { $('#album_editor input[type="submit"]').removeAttr("disabled") }