function hide_dialog() { HideDialog() } function DeleteCommentBegin() { $("div#dialog div#dialog_msg").hide() } function DeleteCommentFailure() { $("div#dialog div#dialog_msg").removeClass("success_message").addClass("error_message").html($("input#w_ajax_fail").val()).fadeIn("fast") } function DeleteCommentSuccess(e) { if (e.Done) { var s = e.CommentId; $("div#comment_" + s).remove(), FadeOutDialog(300) } else $("div#dialog div#dialog_msg").removeClass("success_message").addClass("error_message").html(e.Errors[0]).fadeIn("fast") }