function SaveBegin() { $("#favorites #msg_save").hide() } function SaveFailure() { $("#favorites #msg_save").removeClass("success_message").addClass("error_message").html($("#favorites #ajax_fail").val()).fadeIn("fast") } function SaveSuccess(s) { if (s.Done) $("#favorites #msg_save").removeClass("error_message").addClass("success_message").html(s.Message).fadeIn("fast"); else { var e = $("#msg_errors_title").val(); e += "<ul>"; for (var a = 0; a < s.Errors.length; a++) e += "<li>" + s.Errors[a] + "</li>"; e += "</ul>", $("#favorites #msg_save").removeClass("success_message").addClass("error_message").html(e).fadeIn("fast") } }