add_css('_Header');
add_js('_Header');


// these functions need to be here instead on header.js because they 
//will be called from outside of Header.cshtml view.
function show_hbubbles(new_reqs, new_msgs, new_nots) {
    show_not_bubble(new_nots);
    show_req_bubble(new_reqs);
    show_msg_bubble(new_msgs);
}

function show_not_bubble(count) {
    $('#hbubble_nots').html(count);
    if (count > 0)
        $('#hbubble_nots').show();
    else
        $('#hbubble_nots').hide();
}

function show_req_bubble(count) {
    $('#hbubble_reqs').html(count);
    if (count > 0)
        $('#hbubble_reqs').show();
    else
        $('#hbubble_reqs').hide();
}

function show_msg_bubble(count) {
    $('#hbubble_msgs').html(count);
    if (count > 0)
        $('#hbubble_msgs').show();
    else
        $('#hbubble_msgs').hide();
}

function reset_not_bubble() {
    $('#hbubble_nots').html(0).hide();
}