function MarkAllNotificationsAsReadSuccess(data) {
    $('div#hmi_notifications #notifications_in .notification').removeClass('not_new');
    // call func from _Header.js
    reset_not_bubble();
}

function MarkAllNotificationsAsReadFailure(data) {
    show_error_dialog($('#hmi_notifications input#hid_ajax_fail').val());
}