add_css("_HomePageSuggestions");
//*** load:
$(function () {
    _HomePageSuggestionsLoad();
});

function _HomePageSuggestionsLoad() {
    $(document).scroll(function () {
        var dist = get_scroll();
        if (dist > ($('#hp_suggestions').height() + 15)) {
            $('#hp_pixeladv').css('position', 'fixed').css('top', ($('div#header').height() + 15) + 'px');
        }
        else {
            $('#hp_pixeladv').css('position', 'relative').css('top', '');
        }
    });
}