function SaveManagerSettingBegin() {
    $('#mngr_set_dialog #dialog_msg').removeClass('error_message').removeClass('success_message').hide();
}

function SaveManagerSettingSuccess(data) {
    if (data.Done) {
        $('#mngr_set_dialog #dialog_msg').removeClass('error_message').addClass('success_message').html(data.Message).show();
    }
    else {
        $('#mngr_set_dialog #dialog_msg').removeClass('success_message').addClass('error_message').html(data.Errors[0]).show();
    }
}

function SaveManagerSettingFailure() {
    var msg = $('#mngr_set_dialog input#hid_ajax_fail').val();
    $('#mngr_set_dialog #dialog_msg').removeClass('success_message').addClass('error_message').html(msg).show();
}