function SaveNewsFeedSettingsBegin() { $("#newsfeed_settings #msg_save").removeClass("error_message").removeClass("success_message").hide() } function SaveNewsFeedSettingsSuccess(s) { s.Done ? $("#newsfeed_settings #msg_save").removeClass("error_message").addClass("success_message").html(s.Message).show() : $("#newsfeed_settings #msg_save").removeClass("success_message").addClass("error_message").html(s.Errors[0]).show() } function SaveNewsFeedSettingsFailure() { $("#newsfeed_settings #msg_save").removeClass("success_message").addClass("error_message").html($("#newsfeed_settings #w_ajax_fail").val()).show() }