function DeleteAllNotificationsSuccess(data) {
	if (data.Done) {
	    $('#notifications_in').html('');
	    // call func from _Header.js
	    reset_not_bubble();
	}
	else {
		show_error_dialog(data.Errors[0]);
	}
}

function DeleteAllNotificationsFailure(data) {
    show_error_dialog($('#notifications input#hid_ajax_fail').val());
}

function MarkAllNotificationsAsReadSuccess(data) {
    if (data.Done) {
        $('#notifications_in div.notification').removeClass('not_new');
        // call func from _Header.js
        reset_not_bubble();
    }
    else {
        show_error_dialog(data.Errors[0]);
    }
}

function MarkAllNotificationsAsReadFailure(data) {
    show_error_dialog($('#notifications input#hid_ajax_fail').val());
}