function ToggleMemberBlockBegin() {
    $('#ppbtn_tgl_mem_block').css('visibility', 'hidden');
}

function ToggleMemberBlockSuccess(data) {
    if (data.Done) {
        $('#ppbtn_tgl_mem_block').text(data.ToggleText).css('visibility', 'visible');
    }
    else {
        show_error_dialog(data.Errors[0]);
    }
}

function DeleteMemberProfileCoverBegin() {
    $('#ppbtn_del_prof_cover').css('visibility', 'hidden');
}

function DeleteMemberProfileCoverSuccess(data) {
    if (data.Done) {
        $('div#cover_in').html('<div class="nocover"></div>');
        $('#ppbtn_del_prof_cover').hide();
    }
    else {
        show_error_dialog(data.Errors[0]);
        $('a#ppbtn_del_prof_cover').css('visibility', 'visible');
    }
}

function ShowManagerSettingDialogBegin() {
    var dialog = new Dialog(500, 450, '<div class="loading_dialog"></div>');
    dialog.Show();
}