function show_password_hint(i) { var t = $("#register #password-hint"); t.css("top", i.position().top + "px").css("left", i.position().left - t.width() - 20 + "px").fadeIn("fast") } function hide_hint() { $("#register #password-hint").fadeOut("fast") } function BeginRegister() { $('#register input[type="submit"]').attr("disabled", "disabled") } function FailureRegister() { $("#register .errors").html("<li>" + $("#register #ajax_fail").val() + "</li>") } function SuccessRegister(i) { if (i.Done) window.location.href = "/account/activation?email=" + $('#register input[name="Email"]').val(); else { for (var t = "", e = 0; e < i.Errors.length; e++) t += "<li>" + i.Errors[e] + "</li>"; $("#register .errors").html(t) } } function CompleteRegister() { $('#register input[type="submit"]').removeAttr("disabled") } $(function () { $('#register input[name="Password"]').live("focus", function () { show_password_hint($(this)) }), $('#register input[name="Password"]').live("blur", function () { hide_hint() }) });