function hide_dialog() { HideDialog() } function RemoveFriendBegin() { $("div#dialog div#dialog_msg").hide() } function RemoveFriendFailure() { $("div#dialog div#dialog_msg").removeClass("success_message").addClass("error_message").html($("input#w_ajax_fail").val()).fadeIn("fast") } function RemoveFriendSuccess(e) { e.Done ? (FadeOutDialog(300), $.trim($("input#hid_removing_item").val()).length > 0 && ($("div#friend_item_" + e.FriendId).remove(), $("input#hid_removing_item").val(""))) : $("div#dialog div#dialog_msg").removeClass("success_message").addClass("error_message").html(e.Errors[0]).fadeIn("fast") }