add_css("_VideoPlayer");
add_js("_VideoPlayer");

$(function () {
    _VideoPlayerLoad();
});

function _VideoPlayerLoad() {
    $('.video_player').each(function () {
        if (!$(this).hasClass('loaded')) {
            if ($(this).attr('href') != '#') {
                plugin_player($(this).attr('id'));
            }
            $(this).addClass('loaded');
        }
    });
}