add_css('_ViewMore');
add_js('_ViewMore');
//load:
$(function () {
    _ViewMoreLoad();
});

function _ViewMoreLoad() {
    $(document).scroll(function () {
        var is_there_more = $('a.view_more').length > 0 && $('a.view_more').eq(0).is(":visible");
        if (is_there_more) {
            var dist = get_scroll();
            var vm = $('a.view_more:visible').eq(0);
            var vm_position = vm.position().top;
            var frm = vm.parent();
            var is_auto_allowed = frm.find('input[name="is_auto_alowed"]').eq(0).val();
            if (vm_position - dist < 2000 && is_auto_allowed) {
                frm.find('input[name="is_auto_alowed"]').eq(0).val(false);
                vm.text('').attr('class', 'view_more_progress');
                frm.submit();
            }
        }
    });
}